CREATE TABLE IF NOT EXISTS `ast_words_link` (
  `wl_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '文字链接ID',
  `wl_name` varchar(100) NOT NULL COMMENT '名称',
  `wl_gourl` varchar(100) NOT NULL COMMENT '跳转地址',
